include(dirname(__FILE__) . '/../../include/cli_check.php');
include_once($config['base_path'] . '/plugins/grid/lib/grid_functions.php');

/* process calling arguments */
$parms = $_SERVER['argv'];
array_shift($parms);

global $debug;

$debug = false;
$fix   = false;

foreach($parms as $parameter) {
	@list($arg, $value) = @explode('=', $parameter);

	switch ($arg) {
	case '-d':
	case '--debug':
		$debug = true;
		break;
	case '-f':
	case '--fix':
		$fix = true;
		break;
	case '-v':
	case '-V':
	case '--version':
		display_version();
		exit;
	case '-h':
	case '--help':
		display_help();
		exit;
	case '-g':
	case '--generate':
		make_variable_from_sql();
		exit;
	default:
		print 'ERROR: Invalid Parameter ' . $parameter . "\n\n";
		display_help();
		exit;
	}
}

print "Checking RTM Primary Tables for Engine Compliance\n";

$base_tables = get_tables_from_base();
exit;

function get_tables_from_base() {
	global $config;

	$tarray = get_default_table_structure('10201');

	$tables = db_fetch_assoc('SHOW TABLES');

	if (cacti_sizeof($tables)) {
	foreach($tables as $row) {
		$struct = capture_table($row['Tables_in_cacti']);
		if (cacti_sizeof($struct)) {
			if (isset($struct['type']) && $struct['type'] = 'view') {
				printf("View: %-40s View - Ignoring\n", "'" . $struct['name'] . "'");;
			} else if (isset($tarray[$struct['name']])) {
				printf("Table: %-40s Supported,", "'" . $struct['name'] . "'");
				if ($struct['engine'] == $tarray[$struct['name']]['engine']) {
					print ' Engine Correct, ';
				} else {
					print " Engine Invalid, Spec '" . $tarray[$struct['name']]['engine'] . "' Found '" . $struct['engine'] . "', ";
				}

				if (cacti_sizeof($struct['keys']) == cacti_sizeof($tarray[$struct['name']]['keys'])) {
					print "Keys Correct\n";
				} else {
					$curr_keys = array_rekey($struct['keys'], 'name', 'fields');
					$spec_keys = array_rekey($tarray[$struct['name']]['keys'], 'name', 'fields');

					$extra_key = ', Found Extra:';
					foreach($curr_keys as $keyname => $fields) {
						if (!array_key_exists($keyname, $spec_keys) && !in_array($fields, $spec_keys))
							$extra_key .= '(' . $keyname . '=>' . $fields . ')';
					}

					$missed_key = ', Missed Spec:';
					foreach($spec_keys as $keyname => $fields) {
						if (!array_key_exists($keyname, $curr_keys) && !in_array($fields, $curr_keys))
							$missed_key .= '(' . $keyname . '=>' . $fields . ')';
					}

					$mismatch_key = ', Mismatch Fields:';
					foreach($spec_keys as $keyname => $fields) {
						//print "\n$keyname=>$fields | " . $curr_keys[$keyname] . "\n";
						if (array_key_exists($keyname, $curr_keys) && $curr_keys[$keyname] != $fields)
							$mismatch_key .= '(' . $keyname . '=>' . $fields . ')';
					}

					if ($extra_key != ', Found Extra:' || $missed_key != ', Missed Spec:' || $mismatch_key != ', Mismatch Fields:')
						print 'Keys Issue';
					if ($extra_key != ', Found Extra:')
						print $extra_key;
					if ($missed_key != ', Missed Spec:')
						print $missed_key;
					if ($mismatch_key != ', Mismatch Fields:')
						print $mismatch_key;

					print "\n";
				}
			} else {
				if (preg_match('/^\w+_v\d{3,8}$/', $struct['name'])) {  //substr($struct['name'], -5,2) == '_v') {
					printf("Table: %-40s Partitioned - Ignoring\n", "'" . $struct['name'] . "'");
				} else if (strpos($struct['name'], 'disku_files_raw_') === 0) {
					printf("Table: %-40s DiskUsage Raw - Ignoring\n", "'" . $struct['name'] . "'");
				} else {
					printf("Table: %-40s Unsupported - Ignoring\n", "'" . $struct['name'] . "'");
				}
			}
		}
	}
	}
}

function capture_table($table) {
	$name = '';
	$found_name = false;
	$dengine = '';
	$fengine = '';
	$primary = '';
	$keys    = array();
	$keyval  = 0;
	$tarray  = array();

	$info = db_fetch_row("SHOW CREATE TABLE $table");
	if (isset($info['Create View']) || isset($info['View'])) {
		$tarray['name'] = $table;
		$tarray['type'] = 'view';
		return $tarray;
	}

	if (cacti_sizeof($info)) {
		$create = explode("\n", $info['Create Table']);

		foreach($create as $l) {
			if (substr_count($l, 'CREATE TABLE')) {
				$pos     = strpos($l, 'CREATE TABLE') + 12;
				$revised = str_replace('`','', trim(substr($l, $pos)));
				$parts   = explode(' ', $revised);
				$name    = $parts[0];
			} elseif (substr_count($l, 'PRIMARY KEY')) {
				$pos = strpos($l, 'PRIMARY KEY') + 11;
				$primary = str_replace('`','', trim(substr($l, $pos), " ,()\n\r\0"));
			} elseif (substr_count($l, 'ENGINE=')) {
				$pos = strpos($l, 'ENGINE=') + 7;
				$revised = explode(' ', trim(substr($l, $pos)));
				$engine = $revised[0];
			} elseif (substr_count($l, 'KEY ')) {
				$pos = strpos($l, 'KEY') + 3;
				$revised = str_replace('`','', trim(substr($l, $pos)));
				$revised = explode(' ', $revised);
				$keys[$keyval]['name'] = trim($revised[0]);
				$keys[$keyval]['fields'] = str_replace('`','', trim($revised[1], " ,()\n\r\0"));
				$keyval++;
			}
		}

		$tarray['name'] = $name;
		$tarray['engine'] = $engine;
		$tarray['primary'] = $primary;
		$tarray['keys'] = $keys;
	}

	return $tarray;
}

