	print "-d | --debug     - Display verbose output during execution\n";
	print "-V | --version   - Display version information\n";
	print "-H | --help      - Display this help message\n";
}

function make_variable_from_sql() {
	global $config;
	$file = file($config['base_path'] . '/plugins/grid/cactidump.sql');

	$tarray = array();

	if (cacti_sizeof($file)) {
		$name = '';
		$found_name = false;
		$engine  = '';
		$primary = '';
		$keys    = array();
		$keyval  = 0;
		$tables  = -1;

		foreach($file as $l) {
			if (substr_count($l, 'CREATE TABLE')) {
				if ($tables >= 0 && $engine != '') {
					//Check primary and keys to skip View checking
					$tarray[$name]['name'] = $name;
					$tarray[$name]['primary'] = $primary;
					$tarray[$name]['engine'] = $engine;
					$tarray[$name]['keys'] = $keys;
				}
				$pos     = strpos($l, 'CREATE TABLE') + 12;
				$revised = str_replace('`','', trim(substr($l, $pos)));
				$parts   = explode(' ', $revised);
				$name    = $parts[0];
				$primary = '';
				$engine  = '';
				$keys    = array();
				$keyval  = 0;
				$tables++;
			} elseif (substr_count($l, 'PRIMARY KEY')) {
				$pos = strpos($l, 'PRIMARY KEY') + 11;
				$primary = str_replace('`','', trim(substr($l, $pos), " ,()\n\r\0"));
			} elseif (substr_count($l, 'ENGINE=')) {
				$pos = strpos($l, 'ENGINE=') + 7;
				$revised = explode(' ', trim(substr($l, $pos)));
				$engine = $revised[0];
			} elseif (substr_count($l, 'KEY ')) {
				$pos = strpos($l, 'KEY') + 3;
				$revised = str_replace('`','', trim(substr($l, $pos)));
				$revised = explode(' ', $revised);
				$keys[$keyval]['name'] = trim($revised[0]);
				$keys[$keyval]['fields'] = str_replace('`','', trim($revised[1], " ,()\n\r\0"));
				$keyval++;
			}
		}

		/* get the last table */
		$tarray[$name]['name'] = $name;
		$tarray[$name]['primary'] = $primary;
		$tarray[$name]['engine'] = $engine;
		$tarray[$name]['keys'] = $keys;

		$tarray_string = serialize($tarray);
		file_put_contents('/tmp/variable', $tarray_string);
	}
}

