INSERT INTO `gridalarms_template_expression` VALUES (1,'96e658be6050f393dc2a4cf405602a67','Invalid Dependencies','Shows jobs with invalid dependencies',1,1,'','SELECT gc.clustername, gj.*\nFROM grid_clusters AS gc\nINNER JOIN grid_jobs AS gj\nON gc.clusterid=gj.clusterid\nWHERE gj.stat=\'PEND\'\nAND gj.pendReasons like \'%Dependency%invalid%\'','','',0),(2,'f905ea2357da200f750eb4515e44da45','License Not Used','This script returns a list of license features that are not used in a pre-defined number of days.',2,1,'','','/usr/bin/php |path_cacti|/scripts/ss_alert_lic_license_not_used.php query |input_days|','/usr/bin/php |path_cacti|/scripts/ss_alert_lic_license_not_used.php get |input_days|',0),(3,'fb1383f2ed658c9c105867f1911b1595','lost_and_found','A job is moved to the lost_and_found queue or lost_and_found host.',1,1,'','SELECT gc.clustername, gj.* \nFROM grid_jobs AS gj\nINNER JOIN grid_clusters AS gc\nON gc.clusterid=gj.clusterid\nWHERE queue=\'lost_and_found\'\nOR exec_host=\'lost_and_found\'','','',0),(4,'a310215f97833578eca09f2ca086c61b','Pend Reason like Resource','Job is pending for |input_days| days because a resource it needs is not available.',1,1,'','SELECT gc.clustername, gj.*, gjp.start_time AS pend_start_time, gjpm.reason AS reason\nFROM grid_clusters AS gc\nINNER JOIN grid_jobs AS gj\nON gj.clusterid=gc.clusterid\nINNER JOIN grid_jobs_pendreasons AS gjp\nON gj.clusterid=gjp.clusterid\nAND gj.jobid=gjp.jobid\nAND gj.indexid=gjp.indexid\nAND gj.submit_time=gjp.submit_time\nINNER JOIN grid_jobs_pendreason_maps AS gjpm \nON gjp.issusp=gjpm.issusp AND gjp.reason=gjpm.reason_code AND gjp.subreason=gjpm.sub_reason_code \nWHERE gjpm.reason LIKE \'%(|input_resource|)%\'\nAND gjp.end_time=\'0000-00-00\'\nAND gjp.start_time< SUBDATE(NOW(),INTERVAL |input_days| day)','','',0),(5,'ebde21b2a09028f86e8230b1a374ae66','Pending Reason','Jobs are pending on this |input_reason| reason for |input_days| days.',1,1,'','SELECT gc.clustername, gj.*, gjp.start_time AS pend_start_time\nFROM grid_clusters AS gc\nINNER JOIN grid_jobs AS gj\nON gj.clusterid=gc.clusterid\nINNER JOIN grid_jobs_pendreasons AS gjp\nON gj.clusterid=gjp.clusterid\nAND gj.jobid=gjp.jobid\nAND gj.indexid=gjp.indexid\nAND gj.submit_time=gjp.submit_time\nINNER JOIN grid_jobs_pendreason_maps AS gjpm \nON gjp.issusp=gjpm.issusp \nAND gjp.reason=gjpm.reason_code \nAND gjp.subreason=gjpm.sub_reason_code \nWHERE gjpm.reason LIKE \'%|input_reason|%\'\nAND gjp.end_time=\'0000-00-00\'\nAND gjp.start_time< SUBDATE(NOW(),INTERVAL |input_days| day)\nGROUP BY gjp.clusterid,gjp.jobid,gjp.indexid,gjp.submit_time','','',0),(6,'0e95ddff15f9242958a3c2c23eade417','pending_time_limit','Check jobs that are pending over their effective pending time limits',1,1,'','SELECT gj.clusterid, gc.clustername, jobid, indexid, submit_time, stat, user, \neffectivePendingTimeLimit, pend_time \nFROM grid_jobs AS gj, grid_clusters AS gc \nWHERE gj.clusterid = gc.clusterid \nAND stat in (\'PEND\',\'USUSP\', \'PSUSP\', \'SSUSP\') \nAND effectivePendingTimeLimit >0 \nAND pend_time > effectivePendingTimeLimit \n\n','','',0),(7,'f3414c9f13bd7d0110670d05c517b58a','eligible_pending_time_limit','Check jobs that are pending over their effective elgible pending time limits',1,1,'','SELECT gj.clusterid, gc.clustername, jobid, indexid, submit_time, stat, user, \neffectiveEligiblePendingTimeLimit, \n(pend_time - ineligiblePendingTime)  AS EligiblePendingTime\nFROM grid_jobs AS gj, grid_clusters AS gc \nWHERE gj.clusterid = gc.clusterid \nAND stat in (\'PEND\',\'USUSP\', \'PSUSP\', \'SSUSP\') \nAND effectiveEligiblePendingTimeLimit >0 \nAND (pend_time - ineligiblePendingTime)  > effectiveEligiblePendingTimeLimit \n\n','','',0),(8,'37339d4214e5d8282e8d03278a813d10','disk used on host','disk used over percent on host ',1,1,'','SELECT poller_id, hostname, mountPoint, percentUsed, present\nFROM disku_pollers_filesystems \nJOIN disku_pollers on disku_pollers.id=disku_pollers_filesystems.poller_id \nWHERE present=1 AND percentUsed > |input_percent| AND hostname LIKE \'|input_hostname|\'','','',0);
