INSERT INTO `snmpagent_cache` VALUES ('.1','thold','CACTI-THOLD-MIB','','MODULE-IDENTITY','Node','not-accessible','','Initial version of this MIB module.'),('.1.1','tholdAppl','CACTI-THOLD-MIB','','OBJECT-IDENTITY','Node','not-accessible','','resevered for Thold application data'),('.1.2','tholdStats','CACTI-THOLD-MIB','','OBJECT-IDENTITY','Node','not-accessible','','resevered for statistics'),('.1.3','tholdEvents','CACTI-THOLD-MIB','','OBJECT-IDENTITY','Node','not-accessible','','resevered for events'),('.1.3.1','tholdEventObjects','CACTI-THOLD-MIB','','OBJECT-IDENTITY','Node','not-accessible','','resevered for event attributes'),('.1.3.1.1.0','eventDateRFC822','CACTI-THOLD-MIB','DisplayString','DATA','Scalar','accessible-for-notify','','The curent date/time of when the event was detected by Thold.'),('.1.3.1.10.0','eventCurrentValue','CACTI-THOLD-MIB','DisplayString','DATA','Scalar','accessible-for-notify','','The current value of the data source that caused this event.'),('.1.3.1.11.0','eventHigh','CACTI-THOLD-MIB','DisplayString','DATA','Scalar','accessible-for-notify','','In relation to eventClass this object describes the upper<br>limit for a warning or an alerting condition.<br>If eventCurrentValue is higher than this upper limit<br>an event will be triggered'),('.1.3.1.12.0','eventLow','CACTI-THOLD-MIB','DisplayString','DATA','Scalar','accessible-for-notify','','In relation to eventClass this object describes the lower<br>limit for a warning or an alerting condition.<br>If eventCurrentValue is below this limit  an event will<br>be triggered'),('.1.3.1.13.0','eventThresholdType','CACTI-THOLD-MIB','TcEventThresholdType','DATA','Scalar','accessible-for-notify','','The monitoring type used to verify a threshold<br>condition:<br><br>highLow     - Monitoring if the current value was below a<br>specific upper and above a specific lower limit<br>baseline    - Monitoring based on deviation in percentage<br>for the upper and lower bound threshold against a<br>time reference in the past<br>timebased   - Monitoring if a breach condition was given for a<br>specific number of times within a specific amount of time<br>in the past'),('.1.3.1.14.0','eventNotificationType','CACTI-THOLD-MIB','TcEventNotificationType','DATA','Scalar','accessible-for-notify','','Describes the several conditions detected by THOLD<br>which will end in a notification / alert:<br><br>restoral    - not available (restoral)<br>triggera    - not available (trigger alert)<br>notifyra    - Notify Alert Retrigger<br>notifywa    - Notify Warning<br>notifyal    - Notify Alert<br>notifyrs    - Notify Restoral<br>triggerw    - not available (trigger warning)<br>notifyaw    - Notify Restoral to Warning:<br>This occurs if conditions for an alert state are no<br>longer breached, but the thresholds for a warning state<br>are still exceeded.'),('.1.3.1.15.0','eventStatus','CACTI-THOLD-MIB','TcEventStatus','DATA','Scalar','accessible-for-notify','','Represents the state of a threshold:<br><br>normal      - Threshold has not been exceeded<br>low         - Lower threshold condition breached<br>high        - Upper threshold condition breached'),('.1.3.1.16.0','eventRealertStatus','CACTI-THOLD-MIB','TcEventRealertStatus','DATA','Scalar','accessible-for-notify','','Represents the long-term state of a threshold:<br><br>normal      - Threshold has not been exceeded<br>low         - Lower threshold condition still breached<br>high        - Upper threshold condition still breached'),('.1.3.1.17.0','eventFailDuration','CACTI-THOLD-MIB','Integer32','DATA','Scalar','accessible-for-notify','','Describes the amount of time in seconds the data source<br>is in a breach condition.'),('.1.3.1.18.0','eventFailCount','CACTI-THOLD-MIB','Integer32','DATA','Scalar','accessible-for-notify','','Describes the number of times the data source<br>is in a breach condition.'),('.1.3.1.19.0','eventFailDurationTrigger','CACTI-THOLD-MIB','Integer32','DATA','Scalar','accessible-for-notify','','Describes the amount of time in seconds the data source<br>must be in a breach condition for an alert or warning<br>to be raised.'),('.1.3.1.2.0','eventClass','CACTI-THOLD-MIB','TcEventClass','DATA','Scalar','accessible-for-notify','','Represents the classification of an event:<br><br>info        - threshold item is in normal state<br>warning     - warning condition fulfilled<br>alert       - alerting condition fulfilled'),('.1.3.1.20.0','eventFailCountTrigger','CACTI-THOLD-MIB','Integer32','DATA','Scalar','accessible-for-notify','','Describes the number of times the data source<br>must be in a breach condition for an alert or warning<br>to be raised.'),('.1.3.1.3.0','eventSeverity','CACTI-THOLD-MIB','TcEventSeverity','DATA','Scalar','accessible-for-notify','','The following are the severities an THOLD event<br>can have:<br><br>low         - informational event,<br>event with low impact<br>medium      - warning event,<br>event with medium impact<br>high        - warning event or alert event,<br>event with high impact<br>critical    - alert event,<br>event with critical impact'),('.1.3.1.4.0','eventCategory','CACTI-THOLD-MIB','DisplayString','DATA','Scalar','accessible-for-notify','','To allow a NMS to categorize different SNMP traps THOLD SNMP traps<br>this object can be used to define a custom category<br>like \'disk_usage\', \'link_utilization\' or \'ping_test\'.'),('.1.3.1.5.0','eventSource','CACTI-THOLD-MIB','DisplayString','DATA','Scalar','accessible-for-notify','','Contains the threshold name to identifiy the threshold (source)<br>generating this event.'),('.1.3.1.6.0','eventDescription','CACTI-THOLD-MIB','DisplayString','DATA','Scalar','accessible-for-notify','','Contains a customized event description.'),('.1.3.1.7.0','eventDevice','CACTI-THOLD-MIB','DisplayString','DATA','Scalar','accessible-for-notify','','This item describes the device name this event is related to.'),('.1.3.1.8.0','eventDeviceIp','CACTI-THOLD-MIB','IpAddress','DATA','Scalar','accessible-for-notify','','